INSERT INTO `tq_activity` (`id`, `category_id`, `title`, `description`, `url`, `start_time`, `end_time`, `thumbnail_base_url`, `thumbnail_path`, `content`, `slug`, `created_at`, `updated_at`, `status`, `guize`, `jiangli`, `jiangli_fangfa`, `bg_image_base_url`, `bg_image_path`, `relation_game_id`, `sort`) VALUES
(1, 2, '再战V计划', '新老用户在本页开通或续费即可得到此内容,你的参与将会使激情再次燃起', '', 1450224000, 1451520000, 'http://storage.game4039.dev/source', '1/9v5dKtGSnMhNEuRCj4XGzTyh2cBZCXkx.jpg', '<h3>年费专享</h3><p><img src="http://p9.yx-s.com/d/inn/51048fe6/images/year.jpg"></p><h3>月费专享</h3><p><img src="http://p9.yx-s.com/d/inn/51048fe6/images/month.jpg"></p>', 'zai-zhanv-ji-hua', 1450159349, 1451538573, 1, '<p>充值达到活动指定金额</p><p>可获得对应额度奖励1次</p>', '<p>独享三项特权</p><p>The Exclusive Privilege Of 3 battles</p><p>独享再战礼包</p><p>V计划会员额外享受</p>', '<p>礼包为将激活码形式</p><p>请您点击复制激活码</p><p>在游戏内对应的位置兑换礼包</p>', 'http://storage.game4039.dev/source', '1/EBCFDDnfPAtAQ3bfYR94XoGbu_D9gJkY.jpg', NULL, 0),
(2, 2, '圣诞狂欢曲', '活动期间，每日14点、22点30分均会在安迪摩尔刷新一只圣诞BOSS，有几率掉落高等级装备哦！', '', 1605864420, 1608456420, 'http://storage.game4039.dev/source', '1/3vBHadnPT613CLWXJJrT4XFw184FXQ5o.gif', '<p><strong>动时间：12月25日-12月31日</strong></p><p><strong>活动范围：S1-S150（即留档内测1服-公测140服）</strong></p><p><strong>一、BOSS刷新</strong></p><p>活动期间，每日14点、22点30分均会在安迪摩尔刷新一只圣诞BOSS，有几率掉落高等级装备哦！</p><p><img src="http://webpic.my4399.com/re/cms/newup/1512/241857491216.jpg"></p><p><strong>二、全民齐参与</strong></p><p>活动期间，完成指定任务即可获得对应的奖励</p><p>①挖矿20次，可兑换10个坐骑水晶</p><p>②击杀精英怪20只，可兑换10个龙翼精华</p><p>③参与击杀100级BOSS10只，可兑换10个幸运奖券</p><p>④击杀5000只60级以上的怪物，可兑换500枚绑金</p><p><strong>三、进阶好礼</strong><br></p><p>活动期间，进阶到对应的阶数即可领取相关的奖励</p><p><strong>四、双倍经验</strong><br></p><p>活动期间，全服70级以上的玩家可以享受野外击杀怪物双倍经验的加成，快速升级哦</p><p><img src="http://webpic.my4399.com/re/cms/newup/1512/241859596479.jpg"></p><p><strong>五、登陆豪礼</strong></p><p>活动期间，登陆即可领取登陆奖励：200绑金、副本卷轴*10、坐骑水晶*10、王者龙戒碎片*2、超大银币袋*20</p>', 'sheng-dan-kuang-huan-qu', 1450430846, 1451539732, 1, '<p>活动期间，进阶到对应的阶数即可领取相关的奖励</p><p>活动期间，全服70级以上的玩家可以享受野外击杀怪物双倍经验的加成，快速升级哦<span class="redactor-invisible-space"><br></span></p>', '<p>双倍经验</p><p>进阶好礼</p><p>登陆好礼</p>', '<p>在活动时间段内即可获得对应的收益</p>', 'http://storage.game4039.dev/source', '1/IbY5xYBR0jNAjIrfQsS7oy0L9SxzG-o_.jpg', 1, 1);
